import{_ as a,o,c as m,Q as p}from"./chunks/framework.7404742a.js";const f=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"blog/devtools/mac.md","lastUpdated":null}'),e={name:"blog/devtools/mac.md"},n=p('<h2 id="mac-常用快捷键" tabindex="-1">Mac 常用快捷键 <a class="header-anchor" href="#mac-常用快捷键" aria-label="Permalink to &quot;Mac 常用快捷键&quot;">​</a></h2><p>command+s 【保存】</p><p>command+N （new）【 新建窗口、文档】</p><p>command+O（open）【打开文件，比如在用 wps 时可打开电脑本地的一个文件】</p><p>command+a（all）【全选文件、文本】</p><p>command+T（tab） 【新建标签页】不仅适用于浏览器，也适用于新建访达标签页，onenote 新建分区</p><p>Command+I（info）【显示当前文件简介，如查看文件夹的大小和文件数，图片信息】</p><p>Command+F（find）【搜索查找】</p><p>Command+M（minisize）【最小化窗口 在程序坞】</p><p>Command+H（hide）【隐藏窗口，不在程序坞】</p><p>Command+R（refresh）【刷新】</p><p>Commnad+ shift +5【可以选择截屏和录屏】</p><p>Command+Shift+3 【截屏幕全屏】</p><p>Command+Shift+4，【截取部分屏幕，出现十字，三指拖移，按 esc 取消操作；若截取单个软件窗口，把光标放在软件窗口，再点击空格】</p><p>command+Tab 【切换程序】</p><p>command+逗号【打开软件偏好设置，所有软件的偏好设置都是这个快捷键】</p><p>command+Z【撤销文本输入，操作，如删除一个文件后悔了】</p><p>command+c，然后 command+option+v【移动文件】</p><p>command+空格键 【聚焦搜索】</p><p>Control+command+空格键 【插入表情与符号】</p><p>cheatsheet</p>',21),t=[n];function c(d,s,r,_,i,l){return o(),m("div",null,t)}const C=a(e,[["render",c]]);export{f as __pageData,C as default};
